package com.example.arth.axs175430_asg5;import android.content.Intent;import android.support.v7.app.AppCompatActivity;import android.os.Bundle;import android.support.v7.widget.Toolbar;import android.util.Log;import android.view.Menu;import android.view.MenuInflater;import android.view.MenuItem;import android.widget.ArrayAdapter;import android.widget.ListView;import android.widget.TextView;import android.widget.Toast;import java.util.ArrayList;/***Author - Arth Shah - axs175430 written on 11/1/2018*The Code is written as a part of assigment-5** I have developed an android application which satisfies all the requirements in the assignment document,* has an easy to use layout which follows various design guidelines.** The Application has functionalities to display highscores from highest to lowest alongwith the* most recent one's alongwith that it has the functionality to add new scores.** The Application opens by displaying the ScoreRecords which is the Main Activity for this Application.* The Plus symbol on the Action Bar is used for Adding new Scores.* Whereas the about section gives a general info on the application.*** */public class ScoreRecords extends AppCompatActivity {    private static final String TAG = "ScoreRecords";    @Override    protected void onCreate(Bundle savedInstanceState) {        super.onCreate(savedInstanceState);        setContentView(R.layout.activity_score_records);        Log.d(TAG, "onCreate Started.");        Toolbar toolbar = findViewById(R.id.app_bar);        setSupportActionBar(toolbar);        ListView listView = (ListView) findViewById(R.id.listScores);        ArrayList<ScoreModel>  listOfHighScores = new ArrayList<>();        DataLayer  dataLayer = new DataLayer();        dataLayer.readFromFile();        /*        * Just A Demo to Check working of List Adapter        *        * */        ScoreModel s1 = new ScoreModel("Arth", "2555", "Nov 3", "19:50PM");        ScoreModel s2 = new ScoreModel("Yash", "2556", "Nov 3", "19:59PM");        listOfHighScores.add(s1);        listOfHighScores.add(s2);        HighScoreAdapter highScoreAdapter = new  HighScoreAdapter(this, R.layout.adapter_view_layout, listOfHighScores);        listView.setAdapter(highScoreAdapter);/*        ArrayList<String> score = dataLayer.entries;        TextView t = (TextView) findViewById(R.id.test);        //t.setText(score.get(0));*/    }    @Override    public boolean onCreateOptionsMenu(Menu menu) {        MenuInflater inflater = getMenuInflater();        inflater.inflate(R.menu.menu, menu);        return super.onCreateOptionsMenu(menu);    }    @Override    public boolean onOptionsItemSelected(MenuItem item) {        switch (item.getItemId()) {            case R.id.add_score:                Intent intent1 = new Intent(this, AddRecord.class);                this.startActivity(intent1);                break;            default:                Toast.makeText(getApplicationContext(), "Please Select the correct Option", Toast.LENGTH_SHORT).show();        }        return super.onOptionsItemSelected(item);    }}